    }
    #bloc1
    {
        position: static;
        max-width: 600px;
        margin: 20px auto;
        border: solid darkred 10px;
        box-sizing: border-box;
    }

    #bloc2
    {
        position: relative;
        top: -100px;
        bottom: 50px;
        right: 100px;
        max-width: 600px;
        margin: 20px auto;
        padding: 50px;
        border: solid blue 10px;
        box-sizing: border-box;
    }

    #bloc3
    {
        position: absolute;
        top: 0px;
        left: 0px;
        max-width: 50px;
        margin: 20px auto;
        border: solid darkred 10px;
        box-sizing: border-box;
    }

</style>

